import {Link} from "react-router-dom";
import {useRef} from "react";

export const SignupChild = ({signupParameters}) => {
    const firstname = useRef(null);
    const lastname = useRef(null);
    function execute() {
        console.log(document.getElementById("prenom").value)
    }

    return (
        <div className="row justify-content-center">
            <div className="col-12 col-md-5 col-lg-6 col-xl-4 px-lg-6 my-5 align-self-center">

                <h1 className="display-4 text-center mb-3">
                    {signupParameters.title}
                </h1>

                <p className="text-muted text-center mb-5">
                    {signupParameters.subtitle}
                </p>
                <form>
                    <div className="form-group">
                        <label className="form-label">
                            {signupParameters.firstname.label}
                        </label>
                        <input id={"prenom"} ref={firstname} type={signupParameters.firstname.type} className="form-control"
                               placeholder={signupParameters.firstname.placeholder}/>
                    </div>
                    <button type={"button"} onClick={execute} className="btn btn-lg w-100 btn-primary mb-3">
                        {signupParameters.button.value}
                    </button>
                    <div className="text-center">
                        <small className="text-muted text-center">
                            Vous avez déjà un compte ? <Link to={signupParameters.signinUrl}>Se connecter</Link>.
                        </small>
                    </div>
                </form>
            </div>
            <div className="col-12 col-md-7 col-lg-6 col-xl-8 d-none d-lg-block">
                <div className="bg-cover h-100 min-vh-100 mt-n1 me-n3"
                     style={{backgroundImage: 'url(assets/img/covers/auction-bg1.jpg)'}}></div>
            </div>
        </div>
    )
}
