import {Carousel} from "../childcomponents/Carousel";
import {CarouselCard} from "../childcomponents/CarouselCard";
import {Outbit} from "../childcomponents/Outbit";
import {Crud} from "./Crud";
import {useEffect, useState} from "react";
import axios from "axios";
import {BASE_URL} from "../service/Api-Call";
import {useParams} from "react-router-dom";

export const AuctionDetails = () => {
    const [operations, setOperations] = useState([])
    const [auction, setAuction] = useState({})

    function setToArray(data) {
        let arr = [];
        for (let i = 0; i < data.length; i++) {
            let tempArr = []
            tempArr.push(data[i].id)
            tempArr.push(data[i].customer.lastname + " " + data[i].customer.firstname)
            tempArr.push(data[i].amount)
            tempArr.push(data[i].operationDate)
            arr.push(tempArr)
        }
        return arr;
    }

    useEffect(() => {
        console.log("Mety")
        axios.get(`${BASE_URL}/enchere/8`)
            .then((response) => {
                    console.log(response.data.data)
                    setAuction(response.data.data)
                    let arr = setToArray(response.data.data.operation)
                    setOperations(arr)
                    console.log(arr)
                }
            )
    }, [])
    
    const headers = [
        "ID",
        "Client",
        "Montant",
        "Date d'opération"
    ]
    const names = [
        "id",
        "client",
        "montant",
        "dateoperation"
    ]
    const textValues =
        {
            ajouter: "Ajouter un rechargement",
            modifier: "Accepter le rechargment",
            supprimer: "Supprimer le rechargment",
            btnCreer: "Créer",
        }
    const showDetails = () => {
        console.log("Hello")
    }
    const userDefinedButton = {
        name: "details",
        event: showDetails,
        className: "btn btn-primary",
        value: "Voir details"
    }
    return (<>
        <div className="container">
            <div className="row">
                <div className="col col-xl-8 col-lg-8 col-md-12 col-sm-12 col-12">
                    <Carousel/>
                </div>
                <div className="col col-xl-4 col-lg-4 col-md-12 col-sm-12 col-12">
                    <CarouselCard data={auction}/>
                </div>
            </div>
            <br/>
            <br/>
            <div className="row">
                {/* all cols to 12*/}
                <div className={"col col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12"}>
                    <Outbit/>
                </div>
            </div>
            <div className="row">
                <div className={"col col-xl-12 col-lg-12 col-md-12 col-sm-12 col-12"}>
                    <Crud tableTitle={"Liste des enchères"} headers={headers} names={names} rows={operations}
                          textValues={textValues}
                          crud={false} page_description={"Enchères"} userDefinedButton={userDefinedButton}/>
                </div>
            </div>
        </div>

    </>);
}
