import {MyPieChart} from "../childcomponents/MyPieChart";
import {useEffect, useState} from "react";
import axios from "axios";
import Swal from "sweetalert2";
import {BASE_URL, CONFIG} from "../service/Api-Call";

export const Incomes = () => {
    function getMonthName(monthNumber) {
        const date = new Date();
        date.setMonth(monthNumber - 1);

        return date.toLocaleString('en-US', { month: 'long' });
    }
    const [incomes, setIncomes] = useState(null);
    const setToNameValueObjArray = (data) => {
        let arr = [];
        for (let i = 0; i < data.length; i++) {
            let obj = {}
            obj.name = getMonthName(data[i][2])
            obj.value = data[i][0]
            arr.push(obj)
        }
        return arr;
    }
    useEffect(() => {
        axios.get(`${BASE_URL}/recette/annee/2023`, CONFIG).then((response) => {
            const arr = setToNameValueObjArray(response.data.data)
            console.log(arr)
        }
        ).catch((error) => {
                Swal.fire({
                    icon: 'error',
                    title: 'Désolé...',
                    text: error.response.data.code + ': ' + error.response.data.message,
                })
            }
        )
    }, []);

    const data = [
        {name: 'Group A', value: 400},
        {name: 'Group B', value: 300},
        {name: 'Group C', value: 300},
        {name: 'Group D', value: 200},
        {name: 'Group E', value: 278},
        {name: 'Group F', value: 189},
    ];
    document.getElementById("page-description").innerHTML = "Revenu par mois";
    document.getElementById("createBtn").style.display = "none";
    const pieChartData = {
        data: data,
        title: "Les recettes par an",
        nombretotal: {
            title: "Nombre total d'enchères terminées",
            value: 50
        },
        recettetotal: {
            title: "Montant total gagné",
            value: 100000
        },
        recetteparjour: {
            title: "Montant total gagné par jour",
            value: 100000
        },
        bestelements: {
            title: "Les meilleurs acheteurs",
            name: [
                "Jean Paul",
                "Thierry"
            ]
        }
    }
    return (
        <div className="container-fluid" style={{padding: '10px'}}>
            <div className="row">
                <div className="col-md-12">
                    <MyPieChart data={pieChartData}/>
                </div>
            </div>
        </div>
    )
}
