import {Crud} from "./Crud";
import {useEffect, useState} from "react";
import axios from "axios";
import {BASE_URL, CONFIG} from "../service/Api-Call";
import Swal from "sweetalert2";

export const Auctions = () => {
    const [refunds, setRefunds] = useState([]);

    function setToArray(data) {
        let arr = [];
        for (let i = 0; i < data.length; i++) {
            let tempArr = []
            tempArr.push(data[i].id)
            tempArr.push(data[i].title)
            tempArr.push(data[i].category.name)
            // let dateValidation = data[i].validation_Date;
            // dateValidation=dateValidation.split("T")
            // dateValidation=dateValidation[0]+' '+dateValidation[1];
            // dateValidation=dateValidation.slice(0,19)
            arr.push(tempArr)
        }
        return arr;
    }

    useEffect(() => {
            axios.get(`${BASE_URL}/encheres/0`, CONFIG).then((response) => {
                console.log(response.data.data)
                const arr = setToArray(response.data.data)
                setRefunds(arr)
            }).catch((error) => {
                Swal.fire({
                    icon: 'error',
                    title: 'Désolé...',
                    text: error.response.data.code + ': ' + error.response.data.message,
                })
            })
        },[]
    )
    const headers = [
        "ID",
        "Titre",
        "Catégorie",
        "Vendeur",
        "Statut",
        "Date de début",
        "Date de clôture",
    ]
    const names = [
        "id",
        "titre",
        "categorie",
        "vendeur",
        "statut",
        "date_debut",
        "date_cloture"
    ]
    const textValues =
        {
            ajouter: "Ajouter un rechargement",
            modifier: "Accepter le rechargment",
            supprimer: "Supprimer le rechargment",
            btnCreer: "Créer",
        }
    return (
        <Crud tableTitle={"Liste des enchères"} headers={headers} names={names} rows={refunds} textValues={textValues}
              crud={false} page_description={"Enchères"}/>
    )
}
