import {TableChild} from "../childcomponents/TableChild";
import Swal from "sweetalert2";

export const CrudCategory = () => {
    const headers = [
        "ID",
        "Nom",
        "Description",
    ]
    const rows = [
        [
            "1",
            "Informatique",
            "Tout ce qui concerne l'informatique",
        ],
        [
            "2",
            "Electronique",
            "Tout ce qui concerne l'électronique",
        ],
        [
            "3",
            "Mécanique",
            "Tout ce qui concerne la mécanique",
        ]
    ]
    const updateCategory = (id) => {
        const array= getCategoryData(id);
        console.log(array)
    }

    const deleteCategory = (id) => {
        Swal.fire({
            title: 'Êtes-vous sûr?',
            text: "Vous ne pourrez pas revenir en arrière!",
            icon: 'warning',
            showCancelButton: true,
            confirmButtonColor: '#3085d6',
            cancelButtonColor: '#d33',
            confirmButtonText: 'Oui, supprimez-le!'
        }).then((result) => {
            if (result.isConfirmed) {
                Swal.fire(
                    'Supprimé!',
                    'Votre fichier a été supprimé.',
                    'success'
                )
            }
        }
        )
    }
    const buttons = [
        {
            value:"Modifier",
            className:"btn btn-warning",
            icon:"fas fa-edit",
            event: updateCategory
        },
        {
            value:"Supprimer",
            className:"btn btn-danger",
            icon:"fas fa-trash",
            event: deleteCategory
        }
    ]
    const createBtn= document.getElementById("createBtn");
    document.getElementById("page-description").innerHTML = "Gestion des catégories";
    createBtn.innerHTML = "Créer une catégorie";
    createBtn.onclick = async () => {
        const {value: formValues} = await Swal.fire({
            title: 'Ajouter une catégorie',
            html:
                '<label>Nom</label>' +
                '<input id="swal-input1" class="swal2-input" placeholder="Nom">' +
                '<label>Description</label>' +
                '<input id="swal-input2" class="swal2-input" placeholder="Description">',
            focusConfirm: false,
            preConfirm: () => {
                return [
                    document.getElementById('swal-input1').value,
                    document.getElementById('swal-input2').value
                ]
            }
        })
        if (formValues) {
            if (formValues[0] === "" || formValues[1] === "") {
                Swal.fire({
                    icon: 'error',
                    title: 'Oops...',
                    text: 'Veuillez remplir tous les champs!',
                })
                return;
            }
            const obj = {
                name: formValues[0],
                description: formValues[1]
            }
            Swal.fire(JSON.stringify(obj))
        }
    }
    const getCategoryData = (id) => {
        return rows[id];
    }
    return (
        <div className="container-fluid" style={{padding: '10px'}}>
            <div className="row">
                <div className="col-md-12">
                    <TableChild titre={"Liste des catégories"} headers={headers} rows={rows} buttons={buttons}/>
                </div>
            </div>
        </div>
    )
}
