import * as Utility from "../service/Utility";
import {useEffect, useState} from "react";
import axios from "axios";
import {BASE_URL} from "../service/Api-Call";
import Swal from "sweetalert2";
import {formatDate} from "../service/Utility";

export const CarouselCard = () => {
    const [data, setAuction] = useState(null);
    useEffect(() => {
        axios.get(`${BASE_URL}/enchere/8`).then((response) => {
            setAuction(response.data.data)
            // setOperations(setToArray(response.data.data.operation))
            console.log(response.data.data)
        }).catch((error) => {
                Swal.fire({
                    icon: 'error',
                    title: 'Désolé...',
                    text: error.response.data.code + ': ' + error.response.data.message,
                })
            }
        )
    }, []);

    return (
        <div className="card">
            <div className="card-body">
                <h3 className="card-title">Enchère #{data?.id}</h3>
                <ul>
                    <li>Titre: {data?.title}</li>
                    <li>Description: {data?.description}</li>
                    <li>Catégorie:</li>
                    <ul>
                        <li>Description:</li>
                    </ul>
                    <li>Vendeur: {data?.seller?.lastname} {data?.seller?.firstname}</li>
                    <li>Statut: <b
                        style={data?.status === 0 ? {color: "green"} : {color: "red"}}>{data.status === 0 ? "En cours" : "Terminé"}</b>
                    </li>
                    <li>Date de début: {formatDate(data?.startDate)}</li>
                    <li>Date de clôture: {formatDate(data?.endDate)}</li>
                </ul>
            </div>
        </div>
    );
}
