import {FormChild} from "../childcomponents/FormChild";
import axios from "axios";
import {BASE_URL} from "../service/Api-Call";
import Swal from "sweetalert2";

export const LoginComponent = () => {
    const connection = (email, password) => {
        console.log(email, password)
        const obj = {
            email: email,
            password: password
        }
        axios.post(`${BASE_URL}/admin/login`).then((response) => {
                console.log(response)
            }
        ).catch((error) => {
            Swal.fire({
                icon: 'error',
                title: 'Oops...',
                text: error.message,
            })
            console.log(error)
            }
        )
    }
    const parameters = {
        title: "Connexion",
        subtitle: "Accédez à l'application",
        button: {
            value: "Se connecter"
        },
        event: connection,
        signupUrl: "/signup",
        imageUrl: "assets/img/covers/auction-bg1.jpg"
    }
    const inputs = [
        {
            label: "Email",
            type: "email",
            placeholder: "Entrez votre email",
            name: "email",
            className: "form-control myform"
        },
        {
            label: "Mot de passe",
            type: "password",
            placeholder: "Entrez votre mot de passe",
            name: "password",
            className: "form-control myform"
        }
    ]

    return (
        <FormChild parameters={parameters} inputs={inputs}/>
    )
}
