import {Crud} from "./Crud";
import {useEffect, useState} from "react";
import axios from "axios";
import {BASE_URL, CONFIG} from "../service/Api-Call";
import Swal from "sweetalert2";

export const Refunds = () => {
    const [refunds, setRefunds] = useState([]);

    function setToArray(data) {
        for (let i = 0; i < data.length; i++) {
            
        }
    }

    useEffect(() => {
            axios.get(`${BASE_URL}/refunds`, CONFIG).then((response) => {
                console.log(response.data.data)
                const arr = setToArray(response.data.data)
                setRefunds(arr)
            }).catch((error) => {
                Swal.fire({
                    icon: 'error',
                    title: 'Désolé...',
                    text: error.response.data.code + ': ' + error.response.data.message,
                })
            })
        }
    )
    const headers = [
        "ID",
        "Type de mouvement",
        "Auteur",
        "Prix",
        "Date de demande",
        "Date de validation"
    ]
    const names = [
        "id",
        "typemouvement",
        "auteur",
        "prix",
        "datedemande",
        "datevalidation"
    ]
    const rows = [
        [
            "1",
            "Rechargement",
            "Jean Paul",
            "1000",
            "2021-01-01",
            "2021-01-12"
        ],
        [
            "2",
            "Rechargement",
            "Jean Claude",
            "2000",
            "2021-05-11",
            "2021-05-12"
        ]
    ]
    const textValues =
        {
            ajouter: "Ajouter un rechargement",
            modifier: "Accepter le rechargment",
            supprimer: "Supprimer le rechargment",
            btnCreer: "Créer",
        }
    return (
        <Crud tableTitle={"Liste des rechargements"} headers={headers} names={names} rows={rows} textValues={textValues}
              crud={false} page_description={"Recharges acceptées"}/>
    )
}
