import {Carousel} from "../childcomponents/Carousel";
import {CarouselCard} from "../childcomponents/CarouselCard";
import {useEffect, useState} from "react";
import axios from "axios";
import {BASE_URL} from "../service/Api-Call";
import Swal from "sweetalert2";

export const AuctionDetails = () => {
    const [auction, setAuction] = useState({});
    useEffect(() => {
        axios.get(`${BASE_URL}/enchere/8`).then((response) => {
            setAuction(response.data.data)
        }).catch((error) => {
                Swal.fire({
                    icon: 'error',
                    title: 'Désolé...',
                    text: error.response.data.code + ': ' + error.response.data.message,
                })
            }
        )
    }, []);

    return (
        <div className={"container"}>
            <div className={"row"}>
                {/*col-xl and col-md to 8 and 4, the rest to 12*/}
                <div className={"col-xl-8 col-md-8 col-12"}>
                    <Carousel/>
                </div>
                <div className={"col-xl-4 col-md-4 col-12"}>
                    <CarouselCard/>
                </div>
            </div>
            <div className={"row"}>
                <div className={"col-12"}>
                </div>
            </div>
            <div className={"row"}>
                <div className={"col-12"}>
                </div>
            </div>
        </div>
    );
}
